INSERT INTO `methods` VALUES (1,1,'Do nhân dân bầu',0),(2,1,' Do Quốc hội bầu theo sự giới thiệu của Chủ tịch nước',1),(3,1,'Do Chủ tịch nước giới thiệu ',0),(4,1,' Do Chính phủ bầu',0),(5,2,'Pháp lệnh',0),(6,2,' Luật',0),(7,2,'Hiến pháp',1),(8,2,'Nghị quyết',0),(9,3,'Các nhà làm luật ',0),(10,3,'Quốc hội, nghị viện',0),(11,3,'Nhà nước, giai cấp thống trị ',1),(12,3,'Chính phủ',0),(13,4,'2 kiểu pháp luật',0),(14,4,'3 kiểu pháp luật',0),(15,4,'4 kiểu pháp luật',1),(16,4,'5 kiểu pháp luật',0),(17,5,'Luật tổ chức Quốc hội ',0),(18,5,'Luật tổ chức Chính phủ',0),(19,5,'Luật tổ chức Hội đồng nhân dân và UBND ',0),(21,6,'Áp dụng trong một hoàn cảnh cụ thể. ',0),(22,6,'Áp dụng trong nhiều hoàn cảnh.',1),(23,6,'Cả 2 đều đúng',0),(24,6,'Cả 2 đều sai',0),(25,7,'Thể hiện ý chí chung, phù hợp với lợi ích chung của cộng đồng, thị tộc, bộ lạc; Mang tính manh mún, tản mạn và chỉ có hiệu lực trong phạm vi thị tộc - bộ lạc.',0),(26,7,'Thể hiện ý chí chung, phù hợp với lợi ích chung của cộng đồng, thị tộc, bộ lạc; Mang tính manh mún, tản mạn và chỉ có hiệu lực trong phạm vi thị tộc - bộ lạc.',0),(27,7,'Được thực hiện tự nguyện trên cơ sở thói quen, niềm tin tự nhiên, nhiều khi cũng cần sự cưỡng chế, nhưng không do một bộ máy chuyên nghiệp thực hiện mà do toàn thị tộc tự tổ chức thực hiện.',0),(28,7,'Cả 3 đều đúng',1),(29,8,'Có thể có đầy đủ cả ba yếu tố cấu thành quy phạm pháp luật',0),(30,8,'Có thể chỉ có hai yếu tố cấu thành Quy phạm pháp luật',0),(31,8,'Có thể chỉ có một yếu tố cấu thành Quy phạm pháp luật -> Quy phạm định nghĩa',0),(32,8,'Cả 3 đều đúng',1),(33,9,'Trong các loại nguồn của pháp luật, chỉ có VBPL là nguồn của pháp luật Việt Nam.',0),(34,9,'Trong các loại nguồn của pháp luật, chỉ có VBPL và tập quán pháp là nguồn của pháp luật Việt Nam.',0),(35,9,'Trong các loại nguồn của pháp luật, chỉ có VBPL và tiền lệ pháp là nguồn của pháp luật Việt Nam.',0),(36,9,'Cả 3 đều sai',1),(37,10,'Viện kiểm sát nhân dân ',0),(38,10,' Tòa án nhân dân',1),(39,10,' Hội đồng nhân dân; UBND ',0),(40,10,'Quốc hội ',0),(41,11,'NLPL của các chủ thể là giống nhau.',0),(42,11,'NLPL của các chủ thể là khác nhau.',0),(43,11,'NLPL của các chủ thể có thể giống nhau, có thể khác nhau, tùy theo từng trường hợp cụ thể.',1),(44,11,'cả 3 điều sai',0),(45,12,'Chức năng điều chỉnh các quan hệ xã hội',0),(46,12,'Chức năng xây dựng và bảo vệ tổ quốc',0),(47,12,'Chức năng bảo vệ các quan hệ xã hội',0),(48,12,'Chức năng giáo dục,điều chỉnh các quan hệ xã hội',1),(49,13,'Tính bắt buộc chung (hay tính quy phạm phổ biến)',0),(50,13,'Tính xác định chặt chẽ về mặt hình thức ',0),(51,13,'Cả 2 đều đúng ',0),(52,13,'Cả 2 đều sai',1),(53,14,'Nhà nước Giéc – manh',0),(54,14,'Nhà nước Rôma',1),(55,14,'Nhà nước Aten ',0),(56,14,'Các Nhà nước phương Đông',0),(57,15,'Tính chất giai cấp của nhà nước không đổi nhưng bản \nchất của nhà nước thì thay đổi qua các kiểu nhà nước khác \nnhau',0),(58,15,'Tính chất giai cấp và bản chất của nhà nước không thay \nđổi qua các kiểu nhà nước khác nhau',1),(59,15,'Tính chất giai cấp và bản chất của nhà nước luôn luôn \nthay đổi qua các kiểu nhà nước khác nhau',0),(60,15,'Tính chất giai cấp của nhà nước luôn luôn thay đổi, còn \nbản chất của nhà nước là không đổi qua các kiểu nhà nước \nkhác nhau',0),(61,16,'Thành phố Huế',1),(62,16,'Thành phố Cần Thơ',0),(63,16,'Thành phố Đà Nẵng',0),(64,16,'Thành phố Hải Phòng',0),(65,17,'\"…Nhà nước thực hiện nhất quán chính sách phát triển \nnền kinh tế thị trường có sự quản lý của nhà nước theo định \nhướng XHCN…\"',1),(66,17,'“…Nhà nước thực hiện nhất quán chính sách phát triển \nnền kinh tế thị trường định hướng XHCN…\"',0),(67,17,'\"…Nhà nước thực hiện nhất quán chính sách phát triển \nnền kinh tế thị trường tự do cạnh tranh theo định hướng \nXHCN…\"',0),(68,17,'\"…Nhà nước thực hiện nhất quán chính sách phát triển \nnền kinh tế thị trường kết hợp với kế hoạch phát triển kinh tế \ncủa nhà nước theo định hướng XHCN…\"',0),(69,18,'Là kết quả tất yếu của xã hội loài người, ở đâu có xã hội \nở đó tồn tại nhà nước',0),(70,18,'Là kết quả tất yếu của xã hội có giai cấp ',0),(71,18,'Là do ý chí của các thành viên trong xã hội với mong \nmuốn thành lập nên nhà nước để bảo vệ lợi ích chung',0),(72,18,'Cả A, B và C đều đúng',1),(73,19,'Hòa nhập vào xã hội và phục vụ lợi ích cho các thành \nviên trong xã hội',1),(74,19,'Đứng trên xã hội, tách khỏi xã hội và phục vụ lợi ích cho \nHội đồng thị tộc, tù trưởng, các thủ lĩnh tôn giáo',0),(75,19,'Đứng trên xã hội, tách khỏi xã hội và phục vụ lợi ích cho \ncác thành viên trong xã hội',0),(76,19,'Hòa nhập vào xã hội và phục vụ cho lợi ích của Hội đồng \nthị tộc, tù trưởng, các thủ lĩnh tôn giáo',0),(77,20,'Nhà nước XHCN',1),(78,20,'Nhà nước XHCN; Nhà nước tư sản',0),(79,20,'Nhà nước XHCN; Nhà nước tư sản; Nhà nước phong \nkiến',0),(80,20,'Nhà nước XHCN; Nhà nước tư sản; Nhà nước phong \nkiến; Nhà nước chủ nô',0),(81,21,'3 năm',0),(82,21,'4 năm ',0),(83,21,'5 năm',1),(84,21,'6 năm',0),(85,22,'Nhà nước đơn nhất',1),(86,22,'Nhà nước liên bang',0),(87,22,'Nhà nước liên minh',0),(88,22,'Cả A và C đều đúng',0);
