INSERT INTO `questions` VALUES (1,4,'Theo Hiến pháp Việt Nam 1992, Thủ tướng Chính phủ Nước CHXHCN Việt Nam'),(2,4,'Văn bản nào có hiệu lực cao nhất trong hệ thống pháp luật Việt Nam'),(3,4,'Trong Tuyên ngôn Đảng cộng sản của C.Mác và Ph.Ăngghen viết: “Pháp luật của các ông chỉ là ý chí của giai cấp các ông được đề lên thành luật, cái ý chí mà nội dung là do các điều kiện sinh hoạt vật chất của giai cấp các ông quyết định”.\nĐại từ nhân xưng “các ông” trong câu nói trên muốn chỉ ai'),(4,4,'Lịch sử xã hội loài người đã và đang trải qua mấy kiểu pháp luật'),(5,1,'Đạo luật nào dưới đây quy định một cách cơ bản về chế độ chính trị, chế độ kinh tế, văn hóa, xã hội và tổ chức bộ máy nhà nước.'),(6,4,'Quy phạm pháp luật là cách xử sự do nhà nước quy định để'),(7,4,'Đặc điểm của các quy phạm xã hội (tập quán, tín điều tôn giáo) thời kỳ CXNT'),(8,4,' Mỗi một điều luật'),(9,4,'Khẳng định nào là đúng'),(10,4,'Cơ quan nào có thẩm quyền hạn chế năng lực hành vi của công dân'),(11,4,'Trong một nhà nước'),(12,4,'Chức năng nào không phải là chức năng của pháp luật'),(13,4,'Các thuộc tính của pháp luật là'),(14,4,'Sự xuất hiện của nhà nước cổ đại nào sau đây có \nnguyên nhân là do mâu thuẫn giai cấp gay gắt đến mức \nkhông thể điều hòa được'),(15,4,'Theo học thuyết Mác – Lênin, nhận định nào sau đây \nlà đúng'),(16,4,'Thành phố nào sau đây không phải là thành phố trực \nthuộc trung ương'),(17,4,'Câu nào sau đây đúng với quy định được ghi trong \nĐiều 15 Hiến pháp Việt Nam 1992, đã được sửa đổi, bổ \nsung'),(18,4,'Sự tồn tại của nhà nước'),(19,4,'Quyền lực và hệ thống tổ chức quyền lực trong xã \nhội CXNT'),(20,4,'Ngoài tính chất giai cấp, kiểu nhà nước nào sau đây \ncòn có vai trò xã hội'),(21,4,'Theo Điều lệ ĐCS Việt Nam, nếu không có đại hội \nbất thường, thì mấy năm ĐCS Việt Nam tổ chức Đại hội đại \nbiểu toàn quốc một lần'),(22,4,'Nhà nước CHXHCN Việt Nam có hình thức cấu \ntrúc là');
/*!40000 ALTER TABLE `questions` ENABLE KEYS */;
